const Course = (props) => {
  return (
    <>
      <h1>{props.course}</h1>
    </>
  )
}

const Part = (props) => {
  return (
    <>
      <p>
        {props.part} {props.exercise}
      </p>
    </>
  )
}

const Content = (props) => {
  var listItems = props.list.map((partExercise) =>
    <Part part={partExercise.part} exercise={partExercise.exercise} />
  )

  return (
    <div>
      {listItems}
    </div>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const part1 = 'Fundamentals of React'
  const exercises1 = 10
  const part2 = 'Using props to pass data'
  const exercises2 = 7
  const part3 = 'State of a component'
  const exercises3 = 14

  const list = [
    {id: 1, part: { part1 }, exercise: { exercises1 } },
    {id: 2, part: { part2 }, exercise: { exercises2 } },
    {id: 3, part: { part3 }, exercise: { exercises3 } }
  ]

  return (
    <div>
      <Course course={course} />
      <Content list={list} />
      <p>Number of exercises {exercises1 + exercises2 + exercises3}</p>
    </div>
  )
}

export default App;
